CREATE TABLE `pmslist` (
                           `PMS_SEQ` int NOT NULL AUTO_INCREMENT,
                           `PMS_NO` varchar(100) DEFAULT NULL,
                           `DEPTH1` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '1레벨',
                           `DEPTH2` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '2레벨',
                           `DEPTH3` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '3레벨',
                           `PMS_DESC` varchar(200) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT '' COMMENT '설명',
                           `STRT_DATE` varchar(8) DEFAULT NULL COMMENT '시작일',
                           `END_DATE` varchar(8) DEFAULT NULL COMMENT '종료일',
                           `PROC_RATE` varchar(3) DEFAULT '',
                           PRIMARY KEY (`PMS_SEQ`)
) ENGINE=InnoDB AUTO_INCREMENT=64 DEFAULT CHARSET=utf8mb3;


